import React from 'react';


export default function Form(props) {

    const {
        values,
        submit,
        change,
        disabled,
        errors,
      } = props

      const onChange = evt => {
        /* ðŸ”¥ FIX THIS SO IT ALSO WORKS WITH CHECKBOXES */
        const { name, value, type, checked } = evt.target
        const valueToUse = type === 'checkbox' ? checked : value;
        change(name, valueToUse);
      }

    //   onSubmit={onSubmit}
    return (
        <form> 
            <div>
                <h2>Add User</h2>

                <button disabled={disabled} >submit</button>

                <div>
                {/* ðŸ”¥ RENDER THE VALIDATION ERRORS HERE */}
                {/* <div>{errors.name}</div>
                <div>{errors.email}</div>
                <div>{errors.password}</div> */}
                </div>
            </div>

            <div>
                <h4>General information</h4>

                <label>Username&nbsp;
                <input
                    value={values.name}
                    onChange={onChange}
                    name='username'
                    type='text'
                />
                </label>

                <label>Email
                <input
                    value={values.email}
                    onChange={onChange}
                    name='email'
                    type='text'
                />
                </label>

                <label>Password
                <input
                    onChange={onChange}
                    value={values.role}
                    name='role'
                    type='text'
                />
                </label>

                {/* ////////// RADIO BUTTONS ////////// */}
                {/* <label>Single
                <input type='radio' name='civil' value='single' onChange={onChange} checked={values.civil === 'single'}/>
                </label>
                <label>Married
                <input type='radio' name='civil' value='married' onChange={onChange} checked={values.civil === 'married'}/>
                </label>
            </div>

            <div className='form-group checkboxes'>
                <h4>Hobbies</h4> */}

                {/* ////////// CHECKBOXES ////////// */}

                <label>Agree to Terms of Service
                <input  type='checkbox' name='hiking' checked={values.hiking} 
                onChange={onChange}
                />
                </label>
            </div>
        </form>
    )
}
